  LastMensage(txt:string): string | void{
    let digitos: number = txt.length
    if(digitos > 15){
      let text: string = ""
      for(let i: number = 0; i < 15; i++){
        if(i < 12){
          text += txt[i];
        }
        else{
          text += "."
        }
      }
      return text;
    }
    return txt;
  }
